import axios from "axios";
import Footer from "../Utils/Footer";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

export default function SessionSelection() {
    const [movie, setMovie] = useState([]);
    const { idMovie } = useParams();

    let { days } = movie;

    useEffect(() => {
        const promise = axios.get(`https://mock-api.driven.com.br/api/v5/cineflex/movies/${idMovie}/showtimes`);

        promise.then(response => {
            setMovie(response.data);
        });
    }, [idMovie]);

    if (movie.length === 0) {
        return (
            <div className="loading">
                <img src="./loading.svg" alt="load"/>
                <h1>Carregando...</h1>
            </div>
        );
    }

    return (
        <>
        <div className="title-session"> Selecione o hor√°rio</div>
        <div className="container-session">
            <img className="moviePoster" src={movie.posterURL} alt={movie.title} title={movie.title} />
        </div>



            <div className="content">
                {days.map(session => (
                    <div className="date">
                        <h1>{session.weekday} - {session.date}</h1>

                        <div className="hours">
                            {session.showtimes.map(session =>
                                <Link to={`/seats/${session.id}`}>
                                    <p>{session.name}</p>
                                </Link>
                            )}
                        </div>
                    </div>
                ))}
            </div>

        </>
    );
}
