import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import styled from "styled-components"; 

import Header from "../Utils/Header"
import Footer from "../Utils/Footer";
import Seat from "./Seat";
import Load from "../../assets/img/load.gif"


export default function SeatSelection({ confirm }) {

    const {idSession} = useParams();
    const [sessionDetails, setSessionDetails] = useState();
    const [reserve, setReserveSeats] = useState({ids: []});
    const [fullName, setFullName] = useState("");
    const [identificationDocument, setIdentificationDocument] = useState("");

    useEffect(() => {
        const promise = axios.get(`https://mock-api.driven.com.br/api/v5/cineflex/showtimes/${idSession}/seats`);

        promise.then(response => {
            setSessionDetails(response.data);
        })
    }, [idSession]);



    /*Loading Page*/
    if (sessionDetails === undefined) {
        return (
            <div className="loading">
                <img src={Load} alt="loading..." />
            </div>
        );
    }
    const { movie, day, name } = sessionDetails;


    function holdSeat(idSeat, addArray) {
        if (addArray) {
            setReserveSeats({ ids: [...reserve.ids, idSeat] });
        } else {
            setReserveSeats({
                ids: reserve.ids.filter((idSeatCurrent) => {
                    return idSeatCurrent !== idSeat;
                })
            })
        }
    }

    function handleFullName(i) {
        setFullName(i.target.value);

        setReserveSeats({
            ...reserve,
            "name": i.target.value
        })
    }

    function handleIdentificationDocument(i) {
        setIdentificationDocument(i.target.value);

        setReserveSeats({
            ...reserve,
            "identificationDocument": i.target.value
        })
    }

    function Error() {
        if (reserve.ids.length === 0) {
            alert("Selecione um assento!")
        } 
        else if (fullName.length === 0) {
            alert("Digite o seu nome completo!")
        } 
        else if (identificationDocument.length !== 11) {
            alert("Digite um CPF válido!")
        }
    }

    function confirmation() {
        confirm(reserve, idSession, '')
        axios.post(`https://mock-api.driven.com.br/api/v5/cineflex/seats/book-many`, reserve);
    }

    const correctInformation = reserve.ids.length !== 0 && fullName.length !== 0 && !(identificationDocument.length !== 11);

    return (
        <>
            <Header />
            <main className="container-seat">

                <div className="seats">
                    <h2>Selecione o(s) assento(s)</h2>
                    <div className="seats-to-choose">
                    <Seat isSelected={seat.isSelected} isAvailable={seat.isAvailable} onClick={() => updateSeats(index, seat.name, seat.id)} key={seat.id}>{seat.name}</Seat>

                    </div>

                    <div className="subtitle">
                            <div className="selected">
                                <div className="seat .selected"></div>
                                <p>Selecionado</p>
                            </div>

                            <div className="available">
                                <div className="seat .available"></div>
                                <p>Disponível</p>
                            </div>
                            <div className="unavailable">
                                <div className="seat .unavailable"></div>
                                <p>Indisponível</p>
                            </div>

                        </div>

                </div>

                <div className="username-information">
                    <div className="username">
                        <div className="name">
                            Digite seu nome completo:
                        </div>
                        <input type="text" className="input-name" placeholder='Nome completo' onChange={handleFullName} value={fullName} />
                    </div>
                    <div className="info-identificationDocument">
                        <div className="identificationDocument">
                            Digite seu CPF:
                        </div>
                        <input type="text" className="input-identificationDocument" placeholder='CPF' onChange={handleIdentificationDocument} value={identificationDocument} />
                    </div>

                    {correctInformation === true
                    ? <Link to="/receipt">
                        <Button correctInformation={correctInformation} onClick={() => confirmation()}>
                            Reservar assento(s)
                        </Button>
                    </Link>
                    : <Link to="#">
                        <Button correctInformation={correctInformation} onClick={() => Error()}>
                            Reservar assento(s)
                        </Button>
                    </Link>
                }
                </div>

                <Footer
                    src={movie.posterURL}
                    alt={movie.title}
                    title={movie.title}
                    weekday={day.weekday}
                    time={name}
                />
            </main>
        </>
    );
}

const Button = styled.button`
    background-color: gold;
    width: 250px;
    height: 60px;
    margin: 50px auto;

    font-size: 20px;
    text-aling: center;
    color: rgb(49, 49, 49);
    border-radius: 20px;

    cursor: ${({ correctInformation }) => correctInformation ? 'pointer' : 'not-allowed'};
    opacity: ${({ correctInformation }) => correctInformation ? '1' : '0.6'};
`
