import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import styled from "styled-components"; 

import Header from "../Utils/Header"
import Seat from "./Seat";
import Load from "../../assets/img/load.gif"


export default function SeatSelection({ confirm }) {
    const { idSessao } = useParams();
    const navigate = useNavigate();

    const [customers, setCustomers] = useState([]);
    const [seats, setSeats] = useState([]);
    const [movie, setMovie] = useState([]);
    const [date, setDate] = useState("");
    const [weekday, setWeekday] = useState("");
    const [session, setSession] = useState("");

    useEffect(() => {
        const promise = axios.get(`https://mock-api.driven.com.br/api/v5/cineflex/showtimes/${idSessao}/seats`);
        promise.then(({ data }) => {
            const { seats, movie, day, name } = data;

            const newSeats = seats.map(seat => ({ ...seat, "isSelected": false }));

            setSeats([...newSeats]);
            setMovie(movie);
            setDate(day.date);
            setWeekday(day.weekday);
            setSession(name);
        })
    }, [])

    /*Loading Page*/
    if (sessionDetails === undefined) {
        return (
            <div className="loading">
                <img src={Load} alt="loading..." />
            </div>
        );
    }


    return (
        <>
            <Header />
            <main className="container-seat">

                <div className="seats">
                    <h2>Selecione o(s) assento(s)</h2>
                    <div className="seats-to-choose">
                        {sessionDetails.seats.map(seat => (
                            (seat.isAvailable === true
                                ? <Seat classSeat="seat available" name={seat.name} id={seat.id} handle={holdSeat} />
                                : <Seat classSeat="seat unavailable" name={seat.name} />
                            )
                        ))}

                    </div>

                    <div className="subtitle">
                            <div className="selected">
                                <div className="seat selected"></div>
                                <p>Selecionado</p>
                            </div>

                            <div className="available">
                                <div className="seat available"></div>
                                <p>Disponível</p>
                            </div>
                            <div className="unavailable">
                                <div className="seat unavailable"></div>
                                <p>Indisponível</p>
                            </div>

                        </div>

                </div>

                <div className="username-information">
                    <div className="username">
                        <div className="name">
                            Digite seu nome completo:
                        </div>
                        <input type="text" className="input-name" placeholder='Nome completo' onChange={handleFullName} value={fullName} />
                    </div>
                    <div className="info-identificationDocument">
                        <div className="identificationDocument">
                            Digite seu CPF:
                        </div>
                        <input type="text" className="input-identificationDocument" placeholder='CPF' onChange={handleIdentificationDocument} value={identificationDocument} />
                    </div>

                    {correctInformation === true
                    ? <Link to="/receipt">
                        <Button correctInformation={correctInformation} onClick={() => confirmation()}>
                            Reservar assento(s)
                        </Button>
                    </Link>
                    : <Link to="#">
                        <Button correctInformation={correctInformation} onClick={() => Error()}>
                            Reservar assento(s)
                        </Button>
                    </Link>
                }
                </div>

            </main>
        </>
    );
}

const Button = styled.button`
    background-color: gold;
    width: 250px;
    height: 60px;
    margin: 50px auto;

    font-size: 20px;
    text-aling: center;
    color: rgb(49, 49, 49);
    border-radius: 20px;

    cursor: ${({ correctInformation }) => correctInformation ? 'pointer' : 'not-allowed'};
    opacity: ${({ correctInformation }) => correctInformation ? '1' : '0.6'};
`
